--Question 1e
CREATE OR REPLACE FUNCTION calculate_area
(p_radius NUMBER)
RETURN NUMBER
AS
BEGIN
RETURN round(math_constants.c_pi * (p_radius * p_radius), 3);
END calculate_area;


