--Question 2a
CREATE OR REPLACE FUNCTION parse_firstname 
(p_fullname supplier.contactname%TYPE) 
RETURN VARCHAR2 
IS
    v_firstname supplier.contactname%TYPE;
BEGIN
    v_firstname := substr(p_fullname,1,instr(p_fullname,' ',1,1) - 1);
    RETURN v_firstname;
END parse_firstname;

