--Question 3
CREATE OR REPLACE PACKAGE PKG_SHAPE_CALCULATIONS 
IS
    C_PI CONSTANT NUMBER := ACOS(-1);
    FUNCTION RECTANGLE_AREA
        (P_LENGTH NUMBER,P_WIDTH NUMBER) 
    RETURN NUMBER;
    FUNCTION RECTANGLE_AREA 
        (P_LENGTH NUMBER) 
    RETURN NUMBER;
    FUNCTION CIRCLE_AREA 
        (P_RADIUS NUMBER) 
    RETURN NUMBER;
END PKG_SHAPE_CALCULATIONS;

CREATE OR REPLACE PACKAGE BODY PKG_SHAPE_CALCULATIONS 
IS
    FUNCTION CHECK_VALUE 
        (P_VALUE NUMBER) 
    RETURN BOOLEAN IS V_BOOL BOOLEAN;
    BEGIN
    IF P_VALUE > 0 THEN V_BOOL := TRUE;
    ELSE V_BOOL := FALSE;
    END IF;
    RETURN V_BOOL;
    END CHECK_VALUE;
    FUNCTION RECTANGLE_AREA 
        (P_LENGTH NUMBER,P_WIDTH NUMBER) 
    RETURN NUMBER IS V_AREA NUMBER;
    BEGIN
    IF CHECK_VALUE(P_LENGTH) = TRUE AND CHECK_VALUE(P_WIDTH) = TRUE THEN V_AREA := P_LENGTH * P_WIDTH;
    ELSE V_AREA := -1;
        DBMS_OUTPUT.PUT_LINE('Length and width must be greater than 0');
    END IF;
    RETURN V_AREA;
    EXCEPTION
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT(SQLCODE);
        DBMS_OUTPUT.PUT(': ');
        DBMS_OUTPUT.PUT_LINE(SUBSTR(SQLERRM, 1, 100));
    END RECTANGLE_AREA;
    FUNCTION RECTANGLE_AREA 
    (P_LENGTH NUMBER) 
    RETURN NUMBER IS V_AREA NUMBER;
    BEGIN
    IF CHECK_VALUE(P_LENGTH) = TRUE THEN V_AREA := P_LENGTH * P_LENGTH;
    ELSE V_AREA := -1;
        DBMS_OUTPUT.PUT_LINE('Length must be greater than 0');
    END IF;
    RETURN V_AREA;
    EXCEPTION
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT(SQLCODE);
        DBMS_OUTPUT.PUT(': ');
        DBMS_OUTPUT.PUT_LINE(SUBSTR(SQLERRM, 1, 100));
    END RECTANGLE_AREA;
    FUNCTION CIRCLE_AREA 
    (P_RADIUS NUMBER) RETURN NUMBER IS
    V_AREA NUMBER;
    BEGIN
    IF CHECK_VALUE(P_RADIUS) = TRUE THEN V_AREA := C_PI * P_RADIUS * P_RADIUS;
    ELSE V_AREA := -1;
        DBMS_OUTPUT.PUT_LINE('Radius must be greater than 0');
    END IF;
    RETURN V_AREA;
    EXCEPTION
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT(SQLCODE);
        DBMS_OUTPUT.PUT(': ');
        DBMS_OUTPUT.PUT_LINE(SUBSTR(SQLERRM, 1, 100));
    END CIRCLE_AREA;
END PKG_SHAPE_CALCULATIONS;

--Question 3f
